<?phprequire_once 'autoload.php';session_start();//site.ru/index.php?act=auth&c=User$action = 'action_';$action .=(isset($_GET['act'])) ? $_GET['act'] : 'index';switch ($_GET['c']){	case 'User':		$controller = new C_User();		break;    case 'good':        $controller = new C_Good();        break;    case 'basket':        $controller = new C_Basket();        break;	default:		$controller = new C_Catalog();}$controller->Request($action);